function List(props){
  return(
    <div className='list'>
          <h4> <span onClick={() => {
            props.setIsShow(true)
          }}>{props.title}</span>
            <span onClick={() => {
            let copyLikeCnt = [...props.likeCnt];
            copyLikeCnt[props.idx]++;

