import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const board_list = [
    {
      'boardNo' : 1,
      'title' : '1번 글',
      'content' : '1번 글 내용',
      'writer' : '김자바',
      'regDate' : '2024-02-01'
    },
    {
      'boardNo' : 2,
      'title' : '2번 글',
      'content' : '2번 글 내용',
      'writer' : '홍길동',
      'regDate' : '2024-02-02'
    },
    {
      'boardNo' : 3,
      'title' : '3번 글',
      'content' : '3번 글 내용',
      'writer' : '이자바',
      'regDate' : '2024-02-07'
    },
    {
      'boardNo' : 4,
      'title' : '4번 글',
      'content' : '4번 글 내용',
      'writer' : '김자비',
      'regDate' : '2024-02-07'
    },
    {
      'boardNo' : 5,
      'title' : '5번 글',
      'content' : '5번 글 내용',
      'writer' : '유자바',
      'regDate' : '2024-02-08'
    }
  ];

  let [isShow, setIsShow] = useState(false);

  return (
    <div className="App">
        <div className='board-header'>
            <h3>게시글</h3>
        </div>

        <div className='board-table'>
          <table>
            <thead>
              <tr>
                <td>글번호</td>
                <td>제 목</td>
                <td>작성자</td>
                <td>작성일</td>
              </tr>
            </thead>
            <tbody>
              {
                board_list.map((e , i)=>{
                  return(
                    <tr key={i}>
                      <td>{e.boardNo}</td>
                      <td>{e.title}</td>
                      <td>{e.writer}</td>
                      <td>{e.regDate}</td>
                    </tr>
                  );
                })
              }
            </tbody>
          </table>
        </div>

        {
          isShow ? <Borad/> : ''
        }      


    </div>
  );
}

function Borad(props){
  return(
    <div className='board-footer'>
      <sapn>글번호:</sapn>
      <sapn>제목:</sapn>
      <sapn>내용:</sapn>
      <sapn>작성자:</sapn>
      <sapn>작성일:</sapn>
    </div>
  )
}
export default App;
