      'content' : '5번 글 내용',
      'writer' : '유자바',
      'regDate' : '2024-02-08'
    }
  ];

  let [isShow, setIsShow] = useState(false);
  let [idx , setIdx] = useState(0);

  return (
    <div className="App">
        <div className='board-header'>
            <h3>게시글</h3>
        </div>

        <div className='board-table'>
          <table>
            <thead>
              <tr>
                <td>글번호</td>
                <td>제 목</td>
                <td>작성자</td>
                <td>작성일</td>
              </tr>
            </thead>
            <tbody>
              {
                board_list.map((e , i)=>{
                  return(
                    <Content key={i} borad={e} setIsShow={setIsShow} setIdx={setIdx}/>
                  )
                })
              }
            </tbody>
          </table>
        </div>


          {
            isShow ? <Foofer board_list={board_list} idx ={idx} /> : ''
          }      


    </div>
  );
}

